CREATE DEFINER=`root`@`localhost` FUNCTION `signatureCreate`(sysID int, sigCode varchar(7), sigTypeID int, 
								   name varchar(50), languageID int) RETURNS int(11)
BEGIN
	DECLARE systemExist, sigExits, typeExist, sigNameID int DEFAULT 0;
    
		# CHECK SYSTEM EXIST
    SELECT id INTO systemExist
		FROM systems
        WHERE (id = sysID);
        
	IF (0 = systemExist) THEN
		RETURN 0;
	END IF;
        
		# CHECK SIGNATURE DOES NOT EXIST
    SELECT id INTO sigExits
		FROM signatures
        WHERE (systemID = sysID AND code = sigCode);
        
	IF (sigExits) THEN
		RETURN sigExits;
	END IF;
        
		# CHECK TYPE EXIST
    SELECT id INTO typeExist
		FROM signaturestypes
        WHERE (id = sigTypeID);
        
	IF (0 = typeExist) THEN
		RETURN 0;
	END IF;
        
		# TRY FIND NAME
	IF (languageID > 0) THEN
		SELECT sigNameByByteCode(name, languageID) INTO sigNameID;
	ELSE
		SELECT sigNameByName(name, languageID) INTO sigNameID;
	END IF;
        
	IF (sigNameID = 0) THEN
		RETURN 0;
	END IF;

		# INSERT NEW SIGNATURE
	INSERT INTO signatures(id, systemID, code, typeID, nameID, created, lastCheck)
		VALUES (NULL, sysID, sigCode, sigTypeID, sigNameID, default, default);

	RETURN (SELECT LAST_INSERT_ID());
END ;;
