CREATE DEFINER=`root`@`localhost` FUNCTION `signatureUpdate`(sigID int, sigTypeID int, sigName VARCHAR(50), sigNameBytes VARCHAR(32), languageID int) RETURNS int(11)
BEGIN

	DECLARE checkedTypeID, updates INT DEFAULT 0;
	DECLARE sigNameID INT DEFAULT 5;
    
		# check signature id
	IF (sigTypeID > 1) THEN
		SELECT id INTO checkedTypeID
			FROM signaturesnames
            WHERE (id = sigTypeID);
	END IF;
            
		# if it wrong, exit
	IF (checkedTypeID <= 1) THEN
		RETURN 0;
	END IF;
        
        # if we get name (only when client.scanResult.strenth > 0.75)
	IF (length(sigName) OR length(sigNameBytes)) THEN
    
			# if english, find nameID by name
		IF (languageID = 0) THEN
			SELECT sigNameByName(sigName, languageID) INTO sigNameID;
			
			# if localisated
		ELSE
		
				# try to find by bytecode
			SELECT sigNameByByteCode(sigNameBytes, languageID) INTO sigNameID;
			
				#if not found, try update name of signature 
				#(if it has english name, update localisated byteCode and return anmeID)
			IF (sigNameID=5) THEN
				SELECT signaturesnameUpdateFrom(sigID, sigNameBytes, languageID) INTO sigNameID;
				
			END IF;
			
		END IF;
        
	END IF;
    
		# so, if type and name is ok, update!
	UPDATE signatures 
		SET typeID = checkedTypeID, nameID = sigNameID, lastCheck = now()
        WHERE (id = sigID);

	RETURN 1;
END ;;
