CREATE DEFINER=`root`@`localhost` FUNCTION `systemCreate`(sysID INT, sysName VARCHAR(30), sysSec FLOAT, regID int, regName varchar(30)) RETURNS int(11)
BEGIN

	DECLARE sysExist, regExist INT DEFAULT 0;
    
		# try to get system with id
    SELECT id INTO sysExist
		FROM systems
        WHERE (id = sysID);
        
        # if it exist, exit from fucntion
	IF (sysExist) THEN
		RETURN 0;
	END IF;

		# try to get region with id
	SELECT id INTO regExist
		FROM regions
        WHERE (id = regID);
        
        # if region doesn't exist
	IF (0 = regExist) THEN
    
			# try create region
		SELECT regionCreate(regID, regName) INTO regID;
        
			# if it didn't create, exit from function
        IF (0 = regID) THEN
			RETURN 0;
		END IF;
        
    END IF;
    
		# add system in systems
	INSERT INTO systems(id, name, security, regionID)
		VALUES(sysID, sysName, sysSec, regID);

		# return created systems.id
	RETURN sysID;
END ;;
